mapper.select:

select 'false' as QUERYID,

from T_JS_GQBG
where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}

mapper.delete:

delete from T_JS_GQBG
where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}

mapper.update:

update T_JS_GQBG


    BGQBL = #{bgqbl,jdbcType=DECIMAL},


    BGHBL = #{bghbl,jdbcType=DECIMAL},


    GSSJ = #{gssj,jdbcType=TIMESTAMP},


    ID = #{id,jdbcType=VARCHAR},


where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}


update T_JS_GQBG
set BGQBL = #{bgqbl,jdbcType=DECIMAL},
  BGHBL = #{bghbl,jdbcType=DECIMAL},
  GSSJ = #{gssj,jdbcType=TIMESTAMP},
  ID = #{id,jdbcType=VARCHAR}
where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}

mapper.select:
