mapper.select:

select 'false' as QUERYID,

from T_JS_GDCZ
where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}

mapper.delete:

delete from T_JS_GDCZ
where XYDM = #{xydm,jdbcType=VARCHAR}
  and GD = #{gd,jdbcType=VARCHAR}
  and BGRQ = #{bgrq,jdbcType=TIMESTAMP}

mapper.insert:

insert into T_JS_GDCZ (XYDM, GD, BGRQ, 
  RJE, SJE, GSSJ, RJCZFS, 
  RJCZE, RJCZRQ, SJCZFS, 
  SJCZE, SJCZRQ, ID
  )
values (#{xydm,jdbcType=VARCHAR}, #{gd,jdbcType=VARCHAR}, #{bgrq,jdbcType=TIMESTAMP}, 
  #{rje,jdbcType=DECIMAL}, #{sje,jdbcType=DECIMAL}, #{gssj,jdbcType=TIMESTAMP}, #{rjczfs,jdbcType=VARCHAR}, 
  #{rjcze,jdbcType=DECIMAL}, #{rjczrq,jdbcType=TIMESTAMP}, #{sjczfs,jdbcType=VARCHAR}, 
  #{sjcze,jdbcType=DECIMAL}, #{sjczrq,jdbcType=TIMESTAMP}, #{id,jdbcType=VARCHAR}
  )

mapper.select:
select 'false' as QUERYID, 
from T_JS_BD_GDCZ where xydm=(select a.hcdw_xydm from t_hcrw a where a.id=#{hcrwId})

  and sjly=#{sjly}
