mapper.select:

select 'false' as QUERYID,

from T_HCSX
where ID = #{id,jdbcType=VARCHAR}

select 'false' as QUERYID,

from T_HCSX
where 1=1

    and GSXM = #{gsxm}


    and HCLX = #{hclx}


    and HCXXFL = #{hcxxfl}

order by to_number(CODE), HCLX, QYZZXS, HCXXFL

select 'false' as QUERYID,

from T_HCSX where ID in (
SELECT HCSX_ID FROM T_HCJH_HCSX WHERE HCJH_ID = #{hcjhId}
)
order by to_number(CODE)

select 'false' as QUERYID,

from T_HCSX where ID not in (
SELECT HCSX_ID FROM T_HCJH_HCSX WHERE HCJH_ID = #{hcjhId}
)

    and hclx=1


    and hclx=2

order by to_number(CODE)

select ID from T_HCSX where ID not in (
SELECT HCSX_ID FROM T_HCJH_HCSX WHERE HCJH_ID = #{hcjhId}
)

    and hclx=1


    and hclx=2

order by to_number(CODE)

mapper.update:
update T_HCSX
set HCCL = #{hcclInHcsx,jdbcType=VARCHAR}
where ID = #{hcsxId,jdbcType=VARCHAR}

update T_HCSX

	SET ZXRQ = SYSDATE, ZXSM = #{zxsm,jdbcType=VARCHAR}


	SET ZXRQ = NULL, ZXSM = NULL

where ID = #{hcsxId,jdbcType=VARCHAR}
