mapper.select:

select 'false' as QUERYID,

from T_SCZT
where XYDM = #{xydm,jdbcType=VARCHAR}

select a.id hcrw_id,a.hcjh_id,a.hcdw_xydm,a.hcdw_name,b.nd,b.jhmc,b.jhbh,c.hcsx_id,d.id hccl_id,d.name hccl_name,d.hcsxmc, (select mongo_id from t_hcclmx e where e.hcrw_id=a.id and e.hcsx_id=c.hcsx_id and e.hcdw_xydm=a.hcdw_xydm and e.hcjhnd=a.nd and e.hccl_id=d.id) mongo_id from t_hcrw a,t_hcjh b,t_hcjh_hcsx c,t_hccl d where a.hcjh_id=b.id and b.id=c.hcjh_id and c.hcsx_id=d.HCSX_ID
and a.hcdw_xydm = #{xydm}
and b.nd = #{nd}
ORDER BY a.id
