mapper.select:

select 'false' as QUERYID,

from T_ZFRY
where CODE = #{code,jdbcType=VARCHAR}

select 'false' as QUERYID,
from T_ZFRY where DW_ID IN ( SELECT ID FROM SYS_ORGANIZATION START WITH ID IN ( SELECT ORG_ID FROM T_USER_ORG WHERE USER_ID=#{_userId}) CONNECT BY PRIOR ID=PARENT_ID )
and dw_id like #{dwId} || '%'
and name like #{name} || '%'
and zflx = #{zflx}
and exists(select 1 from t_hcrw b where b.hcjh_id = #{jhid} and (T_ZFRY.code=b.zfry_code1 or T_ZFRY.code=b.zfry_code2))
and USER_ID=#{userId}
order by code
