mapper.select:

select 'false' as QUERYID,

from T_HCCLMX
where ID = #{id,jdbcType=VARCHAR}

select 'false' as QUERYID,

from T_HCCL_FUR
where ID = #{id,jdbcType=VARCHAR}

select 'false' as QUERYID,

from T_HCCLMX WHERE HCRW_ID = #{hcrwId,jdbcType=VARCHAR}
ORDER BY HCSXMC ASC

select 'false' as QUERYID,

from T_HCCLMX
WHERE HCRW_ID = #{hcrwId,jdbcType=VARCHAR}
and hcsx_id = #{hcsxId,jdbcType=VARCHAR}
and hcdw_Xydm = #{hcdwXydm,jdbcType=VARCHAR}
and hcjhnd = #{hcjhnd,jdbcType=VARCHAR}
and hccl_Id = #{hcclId,jdbcType=VARCHAR}
ORDER BY UPLOAD_TIME DESC

select 'false' as QUERYID,

from T_HCCLMX
WHERE HCRW_ID = #{hcrwId,jdbcType=VARCHAR}
and hcsx_id = #{hcsxId,jdbcType=VARCHAR}
and hcdw_Xydm = #{hcdwXydm,jdbcType=VARCHAR}
and hccl_Id = #{hcclId,jdbcType=VARCHAR}
ORDER BY UPLOAD_TIME DESC

select 'false' as QUERYID,

from T_HCCL_FUR WHERE HCRW_ID = #{hcrwId,jdbcType=VARCHAR}
ORDER BY NAME ASC
