mapper.select:

select 'false' as QUERYID,

from T_HCJH
where ID = #{id,jdbcType=VARCHAR}

select 
from T_HCJH a


        AND ND = #{nd}


        AND JHBH = #{jhbh}


        AND GSJHBH = #{gsjhbh}


        AND CXWH = #{cxwh}


        AND NR = #{nr}


        AND FL = #{fl}


        AND DJJGMC = #{hcjgmc}


        AND PLAN_TYPE = #{planType}


        AND JHMC LIKE concat(concat('%', #{jhmc}), '%')

    AND (
      (a.DJJG IN ( SELECT ID FROM SYS_ORGANIZATION START WITH ID IN ( SELECT ORG_ID FROM T_USER_ORG WHERE USER_ID=#{_userId}) CONNECT BY PRIOR ID=PARENT_ID ) and  (select ext1 from sys_user where user_id=#{_userId,jdbcType=VARCHAR})=1)
      or xdr=#{_userId}
    or (a.ID IN (SELECT DISTINCT HCJH_ID FROM t_hcrw rw WHERE rw.zfry_code1 =  (select zfry from sys_user where user_id=#{_userId}) OR rw.zfry_code2 =  (select zfry from sys_user where user_id=#{_userId})) )
    )

ORDER BY

    ${sort}
    ${order},

ND desc,jhbh desc

select 
from T_HCJH a


        AND ND = #{nd}


        AND JHBH = #{jhbh}


        AND GSJHBH = #{gsjhbh}


        AND CXWH = #{cxwh}


        AND NR = #{nr}


        AND FL = #{fl}


        AND DJJGMC = #{hcjgmc}


        AND PLAN_TYPE = #{planType}


        AND JHMC LIKE concat(concat('%', #{jhmc}), '%')

    AND (
    (a.DJJG IN ( SELECT ID FROM SYS_ORGANIZATION START WITH ID IN ( SELECT ORG_ID FROM T_USER_ORG WHERE USER_ID=#{_userId}) CONNECT BY PRIOR ID=PARENT_ID ) and  (select ext1 from sys_user where user_id=#{_userId,jdbcType=VARCHAR})=1)
    or xdr=#{_userId}
    or (a.ID IN (SELECT DISTINCT HCJH_ID FROM t_hcrw rw WHERE rw.zfry_code1 = (select zfry from sys_user where user_id=#{_userId}) OR rw.zfry_code2 =  (select zfry from sys_user where user_id=#{_userId})))
    )

    OR XDR = #{_userId}

ORDER BY

    ${sort}
    ${order},

ND desc,jhbh desc

mapper.update:
update T_HCJH
set SHZT = #{shzt,jdbcType=NUMERIC},
SHR = #{userId},
SHRMC = #{userName}
where ID = #{hcjhId,jdbcType=VARCHAR}
