mapper.select:

select 'false' as QUERYID,

from T_HCRW
where ID = #{id,jdbcType=VARCHAR}

select

from T_HCRW where hcdw_xydm=#{hcdwXydm}
ORDER BY JHWCRQ

select
from T_HCRW where DOC_READY_REPORT_FLAG = 1 AND (ZFRY_CODE1 = (select zfry from sys_user where user_id=#{_userId,jdbcType=VARCHAR}) or ZFRY_CODE2 = (select zfry from sys_user where user_id=#{_userId,jdbcType=VARCHAR}))
AND ND = #{nd}
AND JHBH = #{hcjhId}
AND JHMC LIKE concat(concat('%', #{jhmc}), '%')
AND RLR IS NOT NULL AND RLRQ IS NOT NULL ORDER BY
${sort} ${order},
plan_type desc,jhbh desc,hcdw_xydm

select
from T_HCRW where DOC_READY_REPORT_FLAG = 1 AND QYBM IN ( SELECT ID FROM SYS_ORGANIZATION START WITH ID IN ( SELECT ORG_ID FROM T_USER_ORG WHERE USER_ID=#{_userId,jdbcType=VARCHAR}) CONNECT BY PRIOR ID=PARENT_ID )
AND ND = #{nd}
AND JHBH = #{hcjhId}
AND JHMC LIKE concat(concat('%', #{jhmc}), '%')
AND RLR IS NOT NULL AND RLRQ IS NOT NULL ORDER BY
${sort} ${order},
plan_type desc,jhbh desc,hcdw_xydm

SELECT m.*, n.FR,n.ZZXS, n.LLR, n.LXDH, n.HYFL FROM ( select
from T_HCRW where (ZFRY_CODE1 = (select zfry from sys_user where user_id=#{_userId,jdbcType=VARCHAR}) or ZFRY_CODE2 = (select zfry from sys_user where user_id=#{_userId,jdbcType=VARCHAR}))
AND ND = #{nd}
AND JHBH = #{hcjhId}
AND JHMC LIKE concat(concat('%', #{jhmc}), '%')
AND RLR IS NOT NULL AND RLRQ IS NOT NULL
AND DOC_READY_REPORT_FLAG = #{docReadyReportFlag}
) m LEFT JOIN t_sczt n ON m.hcdw_xydm = n.xydm ORDER BY
${sort} ${order},
hcdw_xydm

select id,lpad('　',(level-1)*2,'　')||name name,level,
(select count(1) from t_hcrw b,t_sczt c
