mapper.select:

select 'false' as QUERYID,

from T_JS_HCRW
where ID = #{id,jdbcType=VARCHAR}

select

from T_JS_HCRW
where (
(ZFRY_CODE =  (select zfry from sys_user where user_id=#{_userId,jdbcType=VARCHAR}) )
or (ZFRY_CODE is null and djjg= (select org_id from sys_user where user_id=#{_userId,jdbcType=VARCHAR}) and (select ext1 from sys_user where user_id=#{_userId,jdbcType=VARCHAR})=1 )
or (DJJG IN ( SELECT ID FROM SYS_ORGANIZATION START WITH ID IN ( SELECT ORG_ID FROM T_USER_ORG WHERE USER_ID=#{_userId,jdbcType=VARCHAR}) CONNECT BY PRIOR ID=PARENT_ID ) and (select ext1 from sys_user where user_id=#{_userId,jdbcType=VARCHAR})=2)
)

    AND PLAN_TYPE = #{docReadyFlag}


    AND nd = #{nd}


    AND HCDW_XYDM = #{hcdwXydm}


    AND HCDW_NAME LIKE concat(concat('%', #{hcdwName}), '%')


    AND JS_GS_FLAG =#{jsGsFlag}


    AND hcjgmc like '%'||#{hcjgmc}||'%'


    AND zfry_code = #{zfryCode}

ORDER BY

    ${sort} ${order},

hcdw_xydm
